import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Chat from './Chat.js';

function App() {
    return (
        <Router>
            <div>
                <h1>Hello, before you can start chatting, please choose a username</h1>
                <Switch>
                    <Route path="/">
                        <Chat />
                    </Route>
                </Switch>
            </div>
            
        </Router>
    )
}

export default App;
