import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import { Link, Router } from 'react-router-dom';    

import { Card, Avatar, Input, Typography} from 'antd';
import 'antd/dist/antd.css'
import './index.css';
import './Login.css';

const { Search } = Input;
const { Text } = Typography;
const { Meta } = Card;





export default class Login extends Component {

    state = {
        userName: '',
        isLoggedIn: false,
    }


    



    render() {
        return (
                <div className="main">
                { this.state.isLoggedIn ?

                    <div class="frame">
                        <div class="title">
                            Start Chatting
                        </div>
                        <div class="option">
                            <div class="opt-1">
                                <i class="ion-compose"></i>
                                Host a Room
                                <Link 
                                    to = "/create"
                                    state = {{ username: "kontoru"}}

                                >
                                    <button class="create" >
                                        Create Room
                                    </button>
                                </Link>
                                
                             
                                
                            </div>
                            <div class="opt-2">
                                <i class="ion-ios-search"></i>
                                Find Room
                                <Link to ='/find'>
                                    <button class="find">
                                        Find Room
                                    </button>  
                                </Link>
                                   
                            </div>
                        </div>

                        <div class="back">
                            <button class="go-back">
                                Back
                            </button>
                        </div>
                    </div>
                : 
                <div style={{ padding: '200px 40px '}}>
                    <Search
                        placeholder="Enter Username"
                        enterButton="Login"
                        size="large"
                        onSearch={ value => this.setState({ isLoggedIn: true, userName: value})}
                    />
                </div>
                
                
                }
                

                </div>
            
        )
    }
}
