import './Login.css';
import React, { Component, useState } from 'react';
import './Create.css';



export default function Create (){

    const [roomName, setRoomName] = useState('');
    const [roomPass, setRoomPass] = useState('');

    createRoomButton = (data) => {
        
    }



    return (
        <div class="frame">
            <div class="option">
                <div class="opt-1">
                    <form>
                        <label>Room Name</label>
                        <input 
                            type="text" 
            
                            value={roomName}  
                            onChange={(e) => setRoomName(e.target.value)}         
                        /> 
                    </form>
                            
                </div>
                <div class="opt-2">
                    <label>Room Password</label>
                    <input 
                        type="text" 
                    
                        value = {roomPass}
                        onChange = {(e) => setRoomPass(e.target.value)}    
                       
                    />
                </div>
            </div>
            <div>
                <p>roomName</p>
                <p>{roomPass}</p>

            </div>

            <div class="back">
                <button class="go-back" onClick={() => this.createRoomButton({
                    roomName, roomPass
                })}>
                    Create
                </button>
            </div>
        </div>


    );

}
